import { useState, useEffect } from "react";

const Clima = ({ region }) => {
  const [dataResponse, setDataResponse] = useState({ weather: ""});

  useEffect(() => {
    fetch(
      `https://api.openweathermap.org/data/2.5/weather?${region}&appid=12f79219938e1bf1c3a59af0fca96d62`
    )
      .then((response) => response.json())
      .then((data) => {
        const obj = {
          weather: data.weather[0].description,
        };
        setDataResponse([...dataResponse, obj]);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [region]);
  console.log(dataResponse.weather);

  return (
    <div>
      <p>{dataResponse.weather}</p>
    </div>
  );
};

export default Clima;
