import { useState, useEffect } from "react";

const Clima = ({ region }) => {
  const [dataResponse, setDataResponse] = useState("");
  useEffect(() => {
    fetch(
      `api.openweathermap.org/data/2.5/weather?${region}&appid={12f79219938e1bf1c3a59af0fca96d62}`, {
        method: 'POST'
      }
    )
      .then((response) => response.json())
      .then((data) => {
        const obj = {
          weather: data[1].wdescriptionord,
        };
        setDataResponse([...dataResponse, obj]);
      });
  }, [region]);
  console.log(dataResponse);

  return <div>Hola</div>;
};

export default Clima;
