import { useState, useEffect } from "react";
import moment from "moment";
import "./Clima.css";
import { useTranslation } from 'react-i18next'

const Clima = ({ region }) => {
  const { t, i18n } = useTranslation();
  const [dataResponse, setDataResponse] = useState({
    weather: "",
    icon: "",
    temp: 0,
    feels_like: 0,
    temp_min: 0,
    temp_max: 0,
    pressure: 0,
    humidity: 0,
    visibility: 0,
    windsSpeed: 0,
    sunrise: "",
    sunset: "",
  });

  const round = (num) => {
    return Math.round(num * 100) / 100;
  };
  useEffect(() => {
    fetch(
      `http://api.openweathermap.org/data/2.5/weather?${region}&appid=12f79219938e1bf1c3a59af0fca96d62`
    )
      .then((response) => response.json())
      .then((data) => {
        const obj = {
          weather: t(data.weather[0].main),
          icon: data.weather[0].icon,
          temp: round(data.main.temp - 273.15),
          feels_like: round(data.main.feels_like - 273.15),
          pressure: data.main.pressure,
          temp_min: round(data.main.temp_min - 273.15),
          temp_max: round(data.main.temp_max - 273.15),
          humidity: data.main.humidity,
          visibility: data.visibility,
          windSpeed: data.wind.speed,
          sunrise: moment(data.sys.sunrise * 1e3)
            .format("hh:mm a")
            .toString(),
          sunset: moment(data.sys.sunset * 1e3)
            .format("hh:mm a")
            .toString(),
        };
        setDataResponse((state) => ({
          ...state,
          name: obj.name,
          weather: obj.weather,
          icon: obj.icon,
          temp: obj.temp,
          feels_like: obj.feels_like,
          temp_min: obj.temp_min,
          temp_max: obj.temp_max,
          pressure: obj.pressure,
          humidity: obj.humidity,
          visibility: obj.visibility,
          windSpeed: obj.windSpeed,
          sunrise: obj.sunrise,
          sunset: obj.sunset,
        }));
      })
      .catch((err) => {
        console.log(err);
      });
  }, [region]);
  i18n.changeLanguage('es_ES');

  console.log('traductor', t)
  return (
    <div>
      <div>{dataResponse.weather}</div>
      <div>
        <img
          src={`http://openweathermap.org/img/wn/${dataResponse.icon}@2x.png`}
        />
      </div>
      <div style={{ padding: 10 }}>Temperatura: {dataResponse.temp}</div>
      <div style={{ padding: 10 }}>Sensación Térmica: {dataResponse.feels_like}</div>
      <div style={{ padding: 10 }}>Temperatura Mínima: {dataResponse.temp_min}</div>
      <div style={{ padding: 10 }}>Temperatura Máxima: {dataResponse.temp_max}</div>
      <div style={{ padding: 10 }}>Presión: {dataResponse.pressure}</div>
      <div style={{ padding: 10 }}>Humedad: {dataResponse.humidity}</div>
      <div style={{ padding: 10 }}>Visibilidad: {dataResponse.visibility}</div>
      <div style={{ padding: 10 }}>Velocidad de viento: {dataResponse.windSpeed}</div>
      <div style={{ padding: 10 }}>Amanecer: {dataResponse.sunrise}</div>
      <div style={{ padding: 10 }}>Atardecer: {dataResponse.sunset}</div>
    </div>
  );
};

export default Clima;
