import { useState, useEffect } from "react";
import moment from "moment";
import Div from "./Div";

const Clima = ({ region }) => {
  const [dataResponse, setDataResponse] = useState([]);

  const round = (num) => {
    return Math.round(num * 100) / 100;
  };
  useEffect(() => {
    fetch(
      `http://api.openweathermap.org/data/2.5/forecast?${region}&appid=12f79219938e1bf1c3a59af0fca96d62`
    )
      .then((response) => response.json())
      .then((data) => {
          const obj=[]
          var startDate=moment(new Date()).add(1, 'day').format('DD-MM-YYYY')
        for (var i = 0; data.list.length; i++) {
          obj.push({
              day: moment(data.list[i].dt * 1e3).format('DD-MM-YYYY'),
            weather: data.list[i].weather[0].main,
            icon: data.list[i].weather[0].icon,
            temp: round(data.list[i].main.temp - 273.15),
            feels_like: round(data.list[i].main.feels_like - 273.15),
            pressure: data.list[i].main.pressure,
            temp_min: round(data.list[i].main.temp_min - 273.15),
            temp_max: round(data.list[i].main.temp_max - 273.15),
            humidity: data.list[i].main.humidity,
            visibility: data.list[i].visibility,
            windSpeed: data.list[i].wind.speed,
            sunrise: moment(data.list[i].sys.sunrise * 1e3)
              .format("hh:mm a")
              .toString(),
            sunset: moment(data.list[i].sys.sunset * 1e3)
              .format("hh:mm a")
              .toString(),
          });
          if(obj.find(x => x.day !== startDate)) {
            setDataResponse({...dataResponse, obj});
          }
        }
      })
      .catch((err) => {
        console.log(err);
      });
  }, [region]);
  console.log("Data Response daily", dataResponse);

  return (
    <div>
      <div style={{ display: "flex" }}>
        <Div padding={5} dataResponse={dataResponse} />
        <Div padding={5} dataResponse={dataResponse} />
        <Div padding={5} dataResponse={dataResponse} />
        <Div padding={5} dataResponse={dataResponse} />
        <Div padding={5} dataResponse={dataResponse} />
      </div>
    </div>
  );
};

export default Clima;
