import { useState, useEffect } from "react";

const Clima = ({ region }) => {
  const [dataResponse, setDataResponse] = useState("lat=-33.1236&lon=-64.3492");
  console.log(region);

  useEffect(() => {
    fetch(`https://api.openweathermap.org/data/2.5/weather?${region}&appid=12f79219938e1bf1c3a59af0fca96d62`)
      .then((response) => response.json())
      .then((data) => {
        const obj = {
          weather: data.weather[0].description,
        };
        setDataResponse([...dataResponse, obj]);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [region]);
  console.log(dataResponse);

  return <div>Hola</div>;
};

export default Clima;
