import { useState, useEffect } from "react";

const Clima = ({ region }) => {
  const [dataResponse, setDataResponse] = useState({
    weather: "",
    icon:'',
    temp: 0,
    feels_like: 0,
    temp_min: 0,
    temp_max: 0,
    pressure: 0,
    humidity: 0,
    visibility: 0,
    windsSpeed: 0,
  });

  const round = (num) => {
    return Math.round(num * 100) / 100;
  };
  useEffect(() => {
    fetch(
      `http://api.openweathermap.org/data/2.5/weather?${region}&appid=12f79219938e1bf1c3a59af0fca96d62`
    )
      .then((response) => response.json())
      .then((data) => {
        const obj = {
          weather: data.weather[0].main,
          icon: data.weather[0].icon,
          temp: round(data.main.temp - 273.15),
          feels_like: round(data.main.feels_like - 273.15),
          pressure: data.main.pressure,
          temp_min: round(data.main.temp_min - 273.15),
          temp_max: round(data.main.temp_max - 273.15),
          humidity: data.main.humidity,
          visibility: data.visibility,
          windSpeed: data.wind.speed,
          sunrise: new Date (data.sunset.sunrise * 1e3),
          sunset:0
        };
        setDataResponse((state) => ({
          ...state,
          weather: obj.weather,
          icon: obj.icon,
          temp: obj.temp,
          feels_like: obj.feels_like,
          temp_min: obj.temp_min,
          temp_max: obj.temp_max,
          pressure: obj.pressure,
          humidity: obj.humidity,
          visibility: obj.visibility,
          windSpeed: obj.windSpeed,
          sunrise: obj.sunrise,
          sunset: obj.sunset
        }));
      })
      .catch((err) => {
        console.log(err);
      });
  }, [region]);
  console.log("Data Response ", dataResponse);

  return (
    <div>
      {dataResponse.weather}
      <br />
      <img src={`http://openweathermap.org/img/wn/${dataResponse.icon}@2x.png`}/>
      Temp: {dataResponse.temp}
      <br />
      Feels Like: {dataResponse.feels_like}
      <br />
      Min: {dataResponse.temp_min}
      <br />
      Max: {dataResponse.temp_max}
      <br />
      Pressure: {dataResponse.pressure}
      <br />
      Humidity: {dataResponse.humidity}
      <br />
      Visibility: {dataResponse.visibility}
      <br />
      Wind Speed: {dataResponse.windSpeed}
    </div>
  );
};

export default Clima;
