import { useState, useEffect } from "react";
import moment from "moment";
import Div from "./Div";

const Clima = ({ region }) => {
  const [dataResponse, setDataResponse] = useState({
    weather: "",
    icon: "",
    temp: 0,
    feels_like: 0,
    temp_min: 0,
    temp_max: 0,
    pressure: 0,
    humidity: 0,
    visibility: 0,
    windsSpeed: 0,
    sunrise: "",
    sunset: "",
  });

  const round = (num) => {
    return Math.round(num * 100) / 100;
  };
  useEffect(() => {
    fetch(
      `http://api.openweathermap.org/data/2.5/forecast?${region}&appid=12f79219938e1bf1c3a59af0fca96d62`
    )
      .then((response) => response.json())
      .then((data) => {
        const obj = {
          weather: data.weather[0].main,
          icon: data.weather[0].icon,
          temp: round(data.main.temp - 273.15),
          feels_like: round(data.main.feels_like - 273.15),
          pressure: data.main.pressure,
          temp_min: round(data.main.temp_min - 273.15),
          temp_max: round(data.main.temp_max - 273.15),
          humidity: data.main.humidity,
          visibility: data.visibility,
          windSpeed: data.wind.speed,
          sunrise: moment(data.sys.sunrise * 1e3)
            .format("hh:mm a")
            .toString(),
          sunset: moment(data.sys.sunset * 1e3)
            .format("hh:mm a")
            .toString(),
        };
        setDataResponse((state) => ({
          ...state,
          name: obj.name,
          weather: obj.weather,
          icon: obj.icon,
          temp: obj.temp,
          feels_like: obj.feels_like,
          temp_min: obj.temp_min,
          temp_max: obj.temp_max,
          pressure: obj.pressure,
          humidity: obj.humidity,
          visibility: obj.visibility,
          windSpeed: obj.windSpeed,
          sunrise: obj.sunrise,
          sunset: obj.sunset,
        }));
      })
      .catch((err) => {
        console.log(err);
      });
  }, [region]);
  console.log("Data Response ", dataResponse);

  return (
    <div>
      <div style={{ display: 'flex'}}>
        <Div padding={5} dataResponse={dataResponse} />
        <Div padding={5} dataResponse={dataResponse} />

      </div>
      <div>
      </div>
      <div>
        <Div padding={5} dataResponse={dataResponse} />
      </div>
      <div>
        <Div padding={5} dataResponse={dataResponse} />
      </div>
      <div>
        <Div padding={5} dataResponse={dataResponse} />
      </div>
    </div>
  );
};

export default Clima;
