import { useState, useEffect } from "react";

const Clima = ({ region }) => {
  const [dataResponse, setDataResponse] = useState({ weather: "" });

  useEffect(() => {
    fetch(
      `http://api.openweathermap.org/data/2.5/weather?${region}&appid=12f79219938e1bf1c3a59af0fca96d62`
    )
      .then((response) => response.json())
      .then((data) => {
        const obj = {
          weather: data.weather[0].description,
        };
        setDataResponse({[weather]: obj });
      })
      .catch((err) => {
        console.log(err);
      });
  }, [region]);
  console.log("Data Response ", dataResponse.obj);

  return (
    <div>
      {dataResponse.obj}
    </div>
  );
};

export default Clima;
