import { useState } from "react";
import Card from './Card'
import Clima from './Clima'
import "./Select.css";

const Select = () => {
  const [value, setValue] = useState("");

  const handleChange = ({ target }) => {
    setValue(target.value);
  };
  return (
    <div>
        <details></details>
      <summary className="select" value={value} name="select" onChange={handleChange}>      --Seleccione Region--</summary>
      <ul>
      <li value=""></li>
        <li value="lat=-33.1236&lon=-64.3492">Rio Cuarto</li>
        <li value="lat=-31.417&lon=-64.183">Cordoba</li>
        <li value="lat=-34.615921&lon=-58.433449">Buenos Aires</li>
        <li value="lat=-40.809502&lon=-65.095512">Las Grutas</li>
        <li value="lat=-32.206640&lon=-64.400210">Embalse</li>
      </ul>
      <Card>
          <div style={{ padding: 20 }}>
            <Clima region={value} /> 
          </div>
        </Card>
    </div>
  );
};

export default Select;
