import { useState, useEffect } from "react";

const Clima = ({ region }) => {
  const [dataResponse, setDataResponse] = useState({ weather: "", temp_min:0, temp_max:0 });

  useEffect(() => {
    fetch(
      `http://api.openweathermap.org/data/2.5/weather?${region}&appid=12f79219938e1bf1c3a59af0fca96d62`
    )
      .then((response) => response.json())
      .then((data) => {
        const obj = {
          weather: data.weather[0].description,
          temp_min: data.main[3],
          temp_max: data.main[4]
        };
        setDataResponse({weather: obj.weather});
      })
      .catch((err) => {
        console.log(err);
      });
  }, [region]);
  console.log("Data Response ", dataResponse);

  return (
    <div>
      {dataResponse.weather}
    </div>
  );
};

export default Clima;
